import { MouseEventHandler } from "react";
import { useState } from "react";
import { Modal } from "src/components/Modal/Modal";
import "./Projects.css";

interface Project {
  name: string;
  url: string;
  video: string;
}

export const projects: Project[] = [
  {
    name: "Perudo",
    url: "https://www.solidesoftware.nl/polliwop/",
    video: "./videos/polliwop.mp4",
  },
  {
    name: "Asteroids",
    url: "https://flopieutd.github.io/asteroids/",
    video: "./videos/asteroids.mp4",
  },
  {
    name: "Mamba",
    url: "https://flopieutd.github.io/mamba/",
    video: "./videos/mamba.mp4",
  },
];

export const Projects = () => {
  const [activeProject, setActiveProject] = useState<Project | null>(null);

  const handleOpenModal = (project: Project) => {
    const body = document.querySelector("body");
    if (!body) {
      return;
    }
    body.style.overflow = "hidden";
    setActiveProject(project);
  };

  const handleCloseModal = () => {
    const body = document.querySelector("body");
    if (!body) {
      return;
    }
    body.style.overflow = "auto";
    setActiveProject(null);
  };

  const handleClickOutside: MouseEventHandler = (event) => {
    if (event.target === event.currentTarget) {
      handleCloseModal();
    }
  };

  return (
    <div className="pet-projects-container">
      {projects.map((project) => (
        <button
          onClick={() => {
            handleOpenModal(project);
          }}
        >
          <video width="320" height="320" autoPlay muted loop>
            <source src={project.video} type="video/mp4" />
            Your browser does not support the video tag.
          </video>
        </button>
      ))}
      {activeProject && (
        <Modal>
          <h3>{activeProject.name}</h3>
          <a href={activeProject.url}>{activeProject.name}</a>
        </Modal>
      )}
    </div>
  );
};
