import { ReactNode } from "react";

interface ModalProps {
  children: ReactNode;
  isActive: boolean;
}

export const Modal = ({ children, isActive }: ModalProps) => {
  if (!isActive) {
    return null;
  }
  return (
    <div className="modal-container" onClick={handleClickOutside}>
      <div className="modal">{children}</div>
    </div>
  );
};
